// Loop over input items and add a new field called 'myNewField' to the JSON of each one
let selectedPosts = []
for (const item of $input.all()) {
  selectedPosts = [...selectedPosts, ...Object.values(item.json.data.Checkboxes)]
}

return { selectedPosts: selectedPosts };

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
let selectedMessages = []
for (const item of $input.all()) {
  selectedMessages = [...selectedMessages, ...item.json.data.Checkboxes]
}

const messages = $('Send a text message').all()
  .map(item => ({ 
    chat: item.json.result.chat.id,
    messageId: item.json.result.message_id,
    message: item.json.result.text
  }))

const messagesToDelete = []

for (const message of messages) {
  let found = false
  for (const selected of selectedMessages) {
    if (message.message.includes(selected)) {
      found = true
      continue
    }
  }

  if (!found) {
    messagesToDelete.push(message)
  }
}


return { messagesToDelete }

let items = [];

for (const item of $input.all()) {
  // Step 1: get the text
  let text = item.json.text || "";

  // Step 2: remove code fences if present
  const jsonString = text
    .replace(/```json/i, "")
    .replace(/```/g, "")
    .trim();

  // Step 3: parse safely
  try {
    const parsed = JSON.parse(jsonString);
    items = [...items, ...parsed];
  } catch (err) {
    console.error("Failed to parse JSON:", err, "\nInput was:\n", jsonString);
  }
}

return {items};
